import React from "react";
/*
interface ICompra {
    comprador: string;
    vendedor: string;
    produto: string;
    data: Date;
    isParcelado: boolean;
    parcelasPagas: number;
    totalParcelas: number;
    valor: number
}

interface ICompraProps {
    url: string;
}

export interface ICompraValues{
    [id: number]: any,
    [comprador: string]: any,
    [vendedor: string]: any,
    [produto: string]: any,
    [data: Date]: any,
    [isParcelado: boolean]: any,
    [parcelasPagas: number]: any,
    [totalParcelas: number]: any,
    [valor: number]: any
}
*/

export default class FormCadastro extends React.Component<{}, {}> {

    constructor(props: any) {

        super(props);
        this.state = {
            nome: ''
        }
        this.alterarNome = this.alterarNome.bind(this);
    }

    alterarNome(event: any) {
        this.setState({nome: event.target.value})
    }

    render() {
        return (
            <div>
                Nome
                <input
                    type="text"
                    onClick={this.alterarNome}
                    placeholder="digite seu nome" />
                <button type="button">gravar</button>
            </div>
        );
    };
}
