import axios from 'axios';
import { useEffect, useState } from 'react';
import { Button, Table, Row, Col } from 'react-bootstrap';
import { BASE_URL } from '../../utils/request';
import { Compra } from '../../utils/types';

export default function TableExtract() {

    const [compras, setCompras] = useState<Compra[]>([]);
    const [refresh, setRefresh] = useState(0);

    useEffect(() => {
        axios.get(`${BASE_URL}/compras`)
            .then(res => {
                const data = res.data;
                setCompras(data);
            });

    }, [refresh]);

    const refreshPage = () => {
        setRefresh(refresh + 1);
    }



    return (
        <>
            <Row className="justify-content-center align-items-center">
                <Col sm={3} >
                    <h1>Extrato</h1>
                </Col>
                <Col>
                    <Button onClick={refreshPage}>Atualizar</Button>
                </Col>
                <Col>
                    <Button >Atualizar parcelas</Button>
                </Col>
            </Row>
            <Table responsive striped hover variant="dark">
                <thead>
                    <tr>
                        <th>Data</th>
                        <th>Comprador</th>
                        <th>Vendedor</th>
                        <th>Produto</th>
                        <th>Valor</th>
                        <th>Parcelas</th>
                        <th>Pagas</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        compras.map(compra => (
                            <tr key={compra.id}>
                                <td>{compra.data}</td>
                                <td>{compra.comprador}</td>
                                <td>{compra.vendedor}</td>
                                <td>{compra.produto}</td>
                                <td>{`R$${compra.valor}`}</td>
                                <td>{compra.totalParcelas}</td>
                                <td>{compra.parcelasPagas}</td>
                            </tr>
                        ))
                    }
                </tbody>
            </Table>
        </>

    );

}
