import { Button, Form } from "react-bootstrap";
import { useState } from 'react'


export default function FormCartao() {
    
    const [cartao, setCartao] = useState({});

    function setInput(novosCampos){
        setCartao(cartao => ({...cartao, ...novosCampos}));
    }    
    return (
        <>
            <Form className="px-5 py-2" >
                <Form.Group controlId="formBasicComprador">
                    <Form.Label>Nome</Form.Label>
                    <Form.Control type="text" placeholder="Nome" onChange={e => setInput({nomeCartao:e.value.target})} />
                </Form.Group>
                <br/>
                <Button className="py-1" variant="primary" type="submit">
                    Cadastrar
                </Button>
            </Form>

        </>
    );
}
