import axios from 'axios';
import { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import { BASE_URL } from '../../utils/request';

type Compra = {
    comprador: string;
    vendedor: string;
    produto: string;
    data: Date;
    isParcelado: boolean;
    parcelasPagas: number;
    totalParcelas: number;
    valor: number;

}


export default function TableExtract() {

    const [compra, setCompra] = useState<Compra>({comprador: '', vendedor: '', produto: '', data: new Date(),
                                                    isParcelado: false, parcelasPagas: 0, totalParcelas: 0, valor: 0});

    useEffect(() => {
        axios.get(`${BASE_URL}/compras`)
            .then(res => {
                const data = res.data;
                setCompra(data);
                console.log(data)
            });

    }, []);


    return (
        <Table responsive striped hover variant="dark">
            <thead>
                <tr>
                    <th>Data</th>
                    <th>Comprador</th>
                    <th>Vendedor</th>
                    <th>Produto</th>
                    <th>Valor</th>
                    <th>Cart√£o</th>
                    <th>Parcelado</th>
                    <th>Parcelas</th>
                    <th>Pagas</th>
                </tr>
            </thead>
            <tbody>
                
            </tbody>
        </Table>

    );

}
