import axios from 'axios';
import { useEffect, useState } from 'react';
import { Button, Table } from 'react-bootstrap';
import { BASE_URL } from '../../utils/request';
import { Compra } from '../../utils/types';

export default function TableExtract() {

    const [compras, setCompras] = useState<Compra[]>([]);

    useEffect(() => {
        axios.get(`${BASE_URL}/compras`)
            .then(res => {
                const data = res.data;
                setCompras(data);
            });

    }, []);



    return (
        <>
            <div className="row justify-content-md-center">
                <div className="col md='auto'">
                    <h1>Extrato de compras</h1>
                </div>
                <div className="col md='auto'">
                    <Button >Clique aqui</Button>
                </div>
            </div>
            <Table responsive striped hover variant="dark">
                <thead>
                    <tr>
                        <th>Data</th>
                        <th>Comprador</th>
                        <th>Vendedor</th>
                        <th>Produto</th>
                        <th>Valor</th>
                        <th>Parcelado</th>
                        <th>Parcelas</th>
                        <th>Pagas</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        compras.map(compra => (
                            <tr key={compra.id}>
                                <td>{compra.data}</td>
                                <td>{compra.comprador}</td>
                                <td>{compra.vendedor}</td>
                                <td>{compra.produto}</td>
                                <td>{`R$${compra.valor}`}</td>
                                <td>{compra.isParcelado.toString()}</td>
                                <td>{compra.totalParcelas}</td>
                                <td>{compra.parcelasPagas}</td>
                            </tr>
                        ))
                    }
                </tbody>
            </Table>
        </>

    );

}
