import axios from 'axios';
import { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import { BASE_URL } from '../../utils/request';

type Compra = {
    comprador: string;
    vendedor: string;
    produto: string;
    data: Date;
    isParcelado: boolean;
    parcelasPagas: number;
    totalParcelas: number;
    valor: number;

}


export default function TableExtract() {

    const [compra, setCompra] = useState([]);

    useEffect(() => {
        axios.get(`${BASE_URL}/compras`)
            .then(res => {
                const data = res.data;
                setCompra(data);
                console.log(compra)
            });

    }, []);


    return (
        <Table responsive striped hover variant="dark">
            <thead>
                <tr>
                    <th>Data</th>
                    <th>Comprador</th>
                    <th>Vendedor</th>
                    <th>Produto</th>
                    <th>Valor</th>
                    <th>Cart√£o</th>
                    <th>Parcelado</th>
                    <th>Parcelas</th>
                    <th>Pagas</th>
                </tr>
            </thead>
            <tbody>
                {compra.map(obj => (
                    <tr key={obj.id}>
                        <td>{obj.data}</td>
                        <td>{obj.comprador}</td>
                        <td>{obj.vendedor}</td>
                        <td>{obj.produto}</td>
                        <td>{obj.totalParcelas}</td>
                        <td>{obj.valor}</td>
                    </tr>
                ))}
            </tbody>
        </Table>

    );

}
