import axios from 'axios';
import { useEffect, useState } from 'react';
import Chart from 'react-apexcharts';
import { BASE_URL } from '../../utils/request';
import { Compra } from '../../utils/types';

const mockData = {
    series: [500, 1500, 800],
    labels: ['DIGIO', 'NEXT', 'NUBANK']
}
const options = {
    legend: {
        show: true
    }
}

export default function PieChart() {

    const [dados, setDados] = useState([])

    useEffect(() => {

        axios.get(`${BASE_URL}/compras`)
            .then((res) => {
                const data = res.data;
                const namesLabels = data.map( (compra: Compra) => compra.produto);
                setDados(namesLabels);
                console.log(namesLabels);
            })
    }, []);

    return (
        <Chart
            options={{ ...options, labels: dados }}
            series={mockData.series}
            type="pie"
            height="450"
        />
    );
}
