import React from "react";
/*
interface ICompra {
    comprador: string;
    vendedor: string;
    produto: string;
    data: Date;
    isParcelado: boolean;
    parcelasPagas: number;
    totalParcelas: number;
    valor: number
}

interface ICompraProps {
    comprador: '';
    vendedor: '';
    produto: '';
    data: Date;
    isParcelado: false;
    parcelasPagas: 0;
    totalParcelas: 0;
    valor: 0;
}
*/
export default class FormCadastro extends React.Component<any, any> {

    constructor(props: any) {

        super(props);
        this.state = {
            nome: 'celiane'
        }
    }

    alterarNome = (evento: any) => {
        evento.addEventListener("click", () => {
            this.setState({ nome: evento.target.value })
            console.log(this.state);
        })
    }

    render() {
        return (
            <div>
                Nome
                <input
                    type="text"
                    onClick={this.alterarNome}
                    placeholder="digite seu nome" />
                <button type="button">gravar</button>
            </div>
        );
    };
}
