import React from "react";
import { Button, Form } from "react-bootstrap";

interface ICompra {
    comprador: string;
    vendedor: string;
    produto: string;
    data: Date;
    isParcelado: boolean;
    parcelasPagas: number;
    totalParcelas: number;
    valor: number
}

interface ICompraProps {
    comprador: '';
    vendedor: '';
    produto: '';
    data: Date;
    isParcelado: false;
    parcelasPagas: 0;
    totalParcelas: 0;
    valor: 0;
}

export default class FormCadastro extends React.Component {

    /*constructor(props: any) {
        super(props);
        this.state = {
            comprador: '',
            vendedor: '',
            produto: '',
            data: new Date(),
            isParcelado: false,
            parcelasPagas: 0,
            totalParcelas: 0,
            valor: 0
        };
    }*/

    constructor(props:any) {
        super(props);
        this.state = {
            nome: ''
        }
        this.aoMudar = evento => {
            this.setState({ nome: evento.target.value });
        }
    }


    render() {
        return (
            <div>
                Nome <input name="nome" type="text" value={this.state.nome} onChange={this.aoMudar} placeholder="digite seu nome" />
                <button type="button">gravar</button>
            </div>
        );
    };
}
