import axios from 'axios';
import { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import { BASE_URL } from '../../utils/request';

type Compra = {
    id: number;
    comprador: string;
    vendedor: string;
    produto: string;
    data: Date;
    isParcelado: boolean;
    parcelasPagas: number;
    totalParcelas: number;
    valor: number;

}


export default function TableExtract() {

    const [compras, setCompras] = useState<Compra[]>([]);

    const dataFormatada = (data: Date) => {
        return ((data.getDate())) + "/" + ((data.getMonth() + 1)) + "/" + data.getFullYear();
    }
    

    useEffect(() => {
        axios.get(`${BASE_URL}/compras`)
            .then(res => {
                const data = res.data;
                setCompras(data);
            });

    }, []);

    

    return (
        <Table responsive striped hover variant="dark">
            <thead>
                <tr>
                    <th>Data</th>
                    <th>Comprador</th>
                    <th>Vendedor</th>
                    <th>Produto</th>
                    <th>Valor</th>
                    <th>Parcelado</th>
                    <th>Parcelas</th>
                    <th>Pagas</th>
                </tr>
                {
                    compras.map(compra => (
                        <tr key={compra.id}>
                            <td>{compra.data}</td>
                            <td>{compra.comprador}</td>
                            <td>{compra.vendedor}</td>
                            <td>{compra.produto}</td>
                            <td>{`R$${compra.valor}`}</td>
                            <td>{compra.isParcelado.toString()}</td>
                            <td>{compra.totalParcelas}</td>
                            <td>{compra.parcelasPagas}</td>
                        </tr>
                    ))
                }
            </thead>
            <tbody>

            </tbody>
        </Table>

    );

}
